style.file:
#
# Default Jamrules
#

if $(UNIX)  { SUFSHR ?= .so ; }
else if $(NT) { SUFSHR ?= .dll ; }

if $(JAM_TOOLSET) { TOOLSET ?= $(JAM_TOOLSET) ; }
TOOLSET ?= GNU ;
VARIANT ?= DEBUG ;

#############################
##
##  Default install locations
##
#############################
PREFIX = installdir ;
BINDIR = $(PREFIX)/bin ;
DATADIR = $(PREFIX)/share ;
DOCDIR = $(DATADIR)/doc ;
HTMLDIR = $(DATADIR)/html ;
INCDIR = $(PREFIX)/include ;
LIBDIR = $(PREFIX)/bin ;
MANDIR = $(DATADIR)/man1 ;

######################################################
##
##  Example definitions for compiler suite (TOOLSETS)
##  It is possible to add definitions here or in a
##  separate file "$(TOOLSET).jam", the NOCARE rule
##  tells jam not to worry if file does not exist.
## 
######################################################
NOCARE $(TOOLSET).jam ;
include $(TOOLSET).jam ;
switch $(TOOLSET)
{
case GNU :
CC = gcc ;
C++ = g++ ;
LINK = $(C++) ;
CCFLAGS += -Wall -std=c99 ;
C++FLAGS += -Wall -std=c++98 ;
SHRFLAGS = -fpic ;
switch $(VARIANT)
{
case RELEASE :
  OPTIM += -O2 -march=native -DDEBUG=0 ;
case * :
  OPTIM = -O0 -ggdb -DDEBUG=1 ;
  CCFLAGS += -pedantic ;
  C++FLAGS += ; # -Weffc++ ;
}
LINKFLAGS += $(OPTIM) ;
CCFLAGS += $(OPTIM) ;
C++FLAGS += $(OPTIM) ;
OPENMP = -fopenmp ;
}

# Set variable for variant-specific build-dir handling
BASE_LOCATE_TARGET = $(BUILDDIR)$(SLASH)$(VARIANT) ;
ALL_LOCATE_TARGET = $(BASE_LOCATE_TARGET) ;

style.template:
ALL_LOCAL_TARGET = $(BASE_LOCAL_TARGET) ;
local SEP_PATH = [ MATCH "([^/]*)" "/([^/]*)/([^/]*)" :  ]
SubDir TOP $(SEP_PATH) ;
local HEREGRIST = <$(SEP_PATH:J=!)> ;

__SRC =
                     $(HEREGRIST)
                     ;
__SRC = $(HEREGRIST) ;

C++FLAGS on $(HEREGRIST)$(SUFEXE) = $(C++FLAGS) -I[  ]  ;
LINKLIBS on $(HEREGRIST)$(SUFEXE) = $(LINKLIBS)  ;

Main $(HEREGRIST)$(SUFEXE) :
  $(__SRC) ;

LinkLibraries $(HEREGRIST)$(SUFEXE) :
	 $(HEREGRIST)[  ]
	;

style.file:
#
# local.jam
#
# Set constants for installation paths
import path ;
path-constant INSTALL_PREFIX : installdir ;
constant BINDIR : bin ; # User programs
constant DATADIR : share ; # Private libraries and static data
constant DOCDIR : [ path.join share doc ] ;
constant HTMLDIR : [ path.join share html ] ;
constant INCDIR : include ;
constant LIBDIR : bin ; # Shared libraries
constant MANDIR  : [ path.join share man1 ] ;
